import { Component, OnInit, Renderer2 } from '@angular/core';

// Affectation des variables
let connectRasp = false;

function getCurrentTime(): string {
  const date: Date = new Date();
  const hours: string = ("0" + date.getHours()).slice(-2);
  const minutes: string = ("0" + date.getMinutes()).slice(-2);
  const seconds: string = ("0" + date.getSeconds()).slice(-2);
  return `${hours}:${minutes}:${seconds}`;
}
setInterval(getCurrentTime, 500);

@Component({
  selector: 'app-testcomponent',
  templateUrl: './testcomponent.component.html',
  styleUrls: ['./testcomponent.component.scss']
})

export class TestcomponentComponent implements OnInit {
  heure!: string;
  degre!: string;
  pression!: string;
  humidite!: string;
  renderer: Renderer2;

  constructor(renderer: Renderer2) {
    this.renderer = renderer;
  }

  ngOnInit() {
    setInterval(() => { this.heure = getCurrentTime(); }, 500);
  }

  // Dès l'appuis du BT 'Simuler une connexion':
  simulConnect(): void {
    setInterval(() => {
      try {
        // Connexion BDD à mettre ICI, si ça fonctionne => connectRasp true
        connectRasp = true;
      } catch (error) {
        alert(error);
        connectRasp = false;
      }
      this.changeValue();
    }, 6000); //Essaye de se connecter pour recup DATA toute les 6s (10x par minute)
  }

  changeValue() {
    //const passageVert = this.renderer.selectRootElement('#changeColor');
    const changeImgRed = this.renderer.selectRootElement('#changeImgRed');
    const changeImgGreen = this.renderer.selectRootElement('#changeImgGreen');

    if (connectRasp) {
        // Le code à exécuter si connectRasp est vrai
        //this.renderer.setStyle(passageVert, 'background-color', '#00ff00');
        changeImgRed.style.display = "none";
        changeImgGreen.style.display = "block";
        this.degre = "20°";
        this.pression = "10Pa";
        this.humidite = "35%";
    } else {
        // Le code à exécuter si connectRasp est faux
        //this.renderer.setStyle(passageVert, 'background-color', '#ff0000');
        changeImgRed.style.display = "block";
        changeImgGreen.style.display = "none";
        this.degre = "";
        this.pression = "";
        this.humidite = "";
    }
  }
}
